import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import useScrollAnimation from '../../hooks/useScrollAnimation';
import { navigationItems } from '../../data/navigation';
import { classNames } from '../../utils/helpers';

const Navigation = () => {
  const { isScrolled } = useScrollAnimation();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <nav className={classNames(
      'fixed top-0 w-full z-50 transition-all duration-500',
      isScrolled 
        ? 'bg-white/90 backdrop-blur-md shadow-lg nav-blur' 
        : 'bg-transparent'
    )}>
      <div className="container mx-auto px-6 py-4">
        <div className="flex items-center justify-between">
          {/* Logo */}
          <Link 
            to="/" 
            className={classNames(
              'text-2xl font-bold transition-colors duration-300',
              isScrolled ? 'text-warm-brown' : 'text-white'
            )}
          >
            Logo
          </Link>

          {/* Desktop Navigation */}
          <div className="hidden md:flex space-x-8">
            {navigationItems.map((item) => (
              <a
                key={item.id}
                href={item.href}
                className={classNames(
                  'transition-colors duration-300 hover:text-warm-brown font-medium',
                  isScrolled ? 'text-gray-700' : 'text-white'
                )}
              >
                {item.label}
              </a>
            ))}
          </div>

          {/* CTA Button */}
          <div className="hidden md:block">
            <button className="btn-primary pulse-glow">
              Solicitar consulta
            </button>
          </div>

          {/* Mobile Menu Button */}
          <button
            className="md:hidden p-2"
            onClick={toggleMobileMenu}
            aria-label="Toggle mobile menu"
          >
            <div className="space-y-1">
              <div className={classNames(
                'w-6 h-0.5 transition-all duration-300',
                isScrolled ? 'bg-warm-brown' : 'bg-white',
                isMobileMenuOpen ? 'rotate-45 translate-y-1.5' : ''
              )}></div>
              <div className={classNames(
                'w-6 h-0.5 transition-all duration-300',
                isScrolled ? 'bg-warm-brown' : 'bg-white',
                isMobileMenuOpen ? 'opacity-0' : ''
              )}></div>
              <div className={classNames(
                'w-6 h-0.5 transition-all duration-300',
                isScrolled ? 'bg-warm-brown' : 'bg-white',
                isMobileMenuOpen ? '-rotate-45 -translate-y-1.5' : ''
              )}></div>
            </div>
          </button>
        </div>

        {/* Mobile Menu */}
        <div className={classNames(
          'md:hidden overflow-hidden transition-all duration-500',
          isMobileMenuOpen ? 'max-h-96 opacity-100' : 'max-h-0 opacity-0'
        )}>
          <div className="py-4 space-y-4">
            {navigationItems.map((item) => (
              <a
                key={item.id}
                href={item.href}
                className={classNames(
                  'block py-2 transition-colors duration-300 hover:text-warm-brown font-medium',
                  isScrolled ? 'text-gray-700' : 'text-white'
                )}
                onClick={() => setIsMobileMenuOpen(false)}
              >
                {item.label}
              </a>
            ))}
            <button className="btn-primary w-full mt-4">
              Solicitar consulta
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navigation;
